var relative = require('./relative'),
    JGL = require('../');
/**
 * @member module:JGL.isRelative
 * @function
 * @param {module:JGL~Query} from
 * @param {module:JGL~Query} to
 * @returns {Boolean}
 */
module.exports = function isRelative (from, to) {
    if (from.length === 0) {
        return true;
    }

    if (to.length < from.length) {
        return false;
    }

    /**
     * The last path that was generated by {@link module:JGL.isRelative}.
     * Useful for not having to re-compute the relative path after testing for
     * relativity.
     * @member module:JGL.LAST_RELATIVE_PATH
     * @type {module:JGL~Query}
     */
    JGL.LAST_RELATIVE_PATH = relative(from, to);
    return JGL.LAST_RELATIVE_PATH.length === to.length - from.length;
};
